import React, { useState, useEffect } from 'react';
import EmailAuthForm from './components/Auth/EmailAuthForm/EmailAuthForm';
import Header from './components/Budget/Header/Header';
import BudgetCard from './components/Budget/BudgetCard/BudgetCard';
import AccountSelection from './components/Budget/AccountSelection/AccountSelection';
import BudgetSettings from './components/Budget/BudgetSettings/BudgetSettings';
import './App.css';

const App = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const checkAuthStatus = () => {
      const token = localStorage.getItem('authToken1');
      if (token) {
        setIsAuthenticated(true);
      }
      setIsLoading(false);
    };

    checkAuthStatus();
  }, []);

  const handleAuthSuccess = () => {
    localStorage.setItem('authToken1', 'authenticated');
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    localStorage.removeItem('authToken1');
    setIsAuthenticated(false);
  };

  if (isLoading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">Загрузка...</div>
      </div>
    );
  }
  if(!isAuthenticated){
    return <EmailAuthForm onAuthSuccess={handleAuthSuccess} />;
  }  

  return (
    <div className="app">
      <Header onLogout={handleLogout} />
      <div className="app-container">
        <div className="main-content">
          <BudgetCard />
          <div className="content-header">
            <h2 className="main-title">Настройки бюджета</h2>
          </div>
          <div className="content-row">
            <BudgetSettings />
            <AccountSelection />
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
