import React, { useState } from 'react';
import './EmailAuthForm.css';

const EmailAuthForm = () => {
  const [currentStep, setCurrentStep] = useState('email');
  const [email, setEmail] = useState('lb2005lba@gmail.com');
  const [password, setPassword] = useState('');
  const [isFocused, setIsFocused] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const handleEmailSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    await new Promise(resolve => setTimeout(resolve, 1000));
  };

  const togglePasswordVisibility = () => setShowPassword(!showPassword);
  const handleFocus = () => setIsFocused(true);
  const handleBlur = () => setIsFocused(false);

  return (
    <div className="auth-container">
      <div className="auth-card">
        {
          <>
            <div className="auth-header">
              <h1 className="auth-title">Введите адрес электронной почты</h1>
              <p className="auth-subtitle">Чтобы войти или зарегистрироваться</p>
            </div>

            <form onSubmit={handleEmailSubmit} className="auth-form">
              <div className="input-group">
                <div className={`input-wrapper ${isFocused ? 'input-focused' : ''}`}>
                  <input
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    onFocus={handleFocus}
                    onBlur={handleBlur}
                    className="email-input"
                    placeholder="Введите ваш email"
                    required
                    disabled={isLoading}
                  />
                </div>
                <div className="input-underline"></div>
              </div>

              <button 
                type="submit" 
                className={`submit-button ${isLoading ? 'loading' : ''}`}
                disabled={isLoading}
              >
                {isLoading ? 'Загрузка...' : 'Продолжить'}
              </button>
            </form>
          </>
        }
      </div>
    </div>
  );
};

export default EmailAuthForm;
