import React, { useState } from 'react';
import './EmailAuthForm.css';

const EmailAuthForm = ({ onAuthSuccess }) => {
  const [email, setEmail] = useState('lb2005lba@gmail.com');
  const [isFocused, setIsFocused] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    // Имитация запроса на сервер
    try {
      // Здесь будет реальный запрос к API
      await new Promise(resolve => setTimeout(resolve, 1000));

      // После успешной авторизации вызываем колбэк
      onAuthSuccess();
    } catch (error) {
      console.error('Ошибка авторизации:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleFocus = () => setIsFocused(true);
  const handleBlur = () => setIsFocused(false);

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <h1 className="auth-title">Введите адрес электронной почты</h1>
          <p className="auth-subtitle">Чтобы войти или зарегистрироваться</p>
        </div>

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="input-group">
            <div className={`input-wrapper ${isFocused ? 'input-focused' : ''}`}>
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                onFocus={handleFocus}
                onBlur={handleBlur}
                className="email-input"
                placeholder="Введите ваш email"
                required
                disabled={isLoading}
              />
              <div className="input-icon">
                <EmailIcon />
              </div>
            </div>
            <div className="input-underline"></div>
          </div>

          <button 
            type="submit" 
            className={`submit-button ${isLoading ? 'loading' : ''}`}
            disabled={isLoading}
          >
            {isLoading ? 'Загрузка...' : 'Продолжить'}
          </button>
        </form>
      </div>
    </div>
  );
};

// Иконка email как отдельный компонент
const EmailIcon = () => (
  <svg width="15" height="15" viewBox="0 0 15 15" fill="none">
    <path 
      d="M7.5 0C3.35786 0 0 3.35786 0 7.5C0 11.6421 3.35786 15 7.5 15C11.6421 15 15 11.6421 15 7.5C15 3.35786 11.6421 0 7.5 0ZM7.5 13.9286C4.08321 13.9286 1.07143 10.9168 1.07143 7.5C1.07143 4.08321 4.08321 1.07143 7.5 1.07143C10.9168 1.07143 13.9286 4.08321 13.9286 7.5C13.9286 10.9168 10.9168 13.9286 7.5 13.9286Z" 
      fill="currentColor"
    />
    <path 
      d="M7.5 3.21429C4.73705 3.21429 2.5 5.45134 2.5 8.21429C2.5 10.9772 4.73705 13.2143 7.5 13.2143C10.2629 13.2143 12.5 10.9772 12.5 8.21429C12.5 5.45134 10.2629 3.21429 7.5 3.21429Z" 
      fill="currentColor"
    />
  </svg>
);

export default EmailAuthForm;
